import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import { Row } from 'react-bootstrap';

export const Keep = () => {
    const [show, setShow] = useState(false);
    const toggle = () => {
        setShow((!show) ? true : false)
    }
    return (
        <section className="mt-5">
            <Container>
                <Row className='justify-content-center flex-column align-items-lg-center '>
                    <input type="text" className='bg-white text-dark' style={{ width: "450px", height: "60px" }}
                        placeholder="Title" onClick={toggle} />
                    <Row className='justify-content-center align-items-lg-center '>
                        <textarea className='bg-white text-dark' placeholder="Take a note..." {...show ? {
                            style: {
                                display: "block",
                                width: "450px",
                                height: "100px",
                                resize: "none"
                            }
                        } : { style: { display: "none" } }} />

                        <button className='btn btn-primary mt-3'
                            {...show ? {
                                style: {
                                    display: "block",
                                    width: "40px",
                                    height: "40px",
                                    borderRadius: "50%",
                                    position: "absolute",
                                    right:"410px"
                                }
                            } : { style: { display: "none" } }}>âž•</button>
                    </Row>
                </Row>
            </Container>
        </section>
    )
}
