import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import { Row } from 'react-bootstrap';
// import { List } from './List';

export const Keep = () => {
    const [show, setShow] = useState(false);
    const toggle = () => {
        setShow((!show) ? true : false)
    }

    const [data, setData] = useState([]);
    const [data2, setData2] = useState("");

    const showData = (e) => {
        setData(e.target.value)
    }

    const showData2 = (e) => {
        setData2(e.target.value)
    }
    return (
        <section className="mt-5">
            <Container>
                <Row className='justify-content-center flex-column align-items-lg-center '>
                    <input type="text" name="title" className='bg-white text-dark'
                        style={{
                            width: "450px",
                            height: "60px",
                            boxShadow: "-17px 13px 36px -10px rgba(0,0,0,0.43)",
                            border: "none",
                        }}
                        placeholder="Title" onClick={toggle} onChange={showData} />
                    <Row className='justify-content-center align-items-lg-center '>
                        <textarea className='bg-white text-dark' placeholder="Take a note..." {...show ? {
                            style: {
                                display: "block",
                                width: "450px",
                                height: "100px",
                                resize: "none",
                                border: "none",
                                boxShadow: "-17px 13px 36px -10px rgba(0,0,0,0.43)"
                            }
                        } : { style: { display: "none" } }} onChange={showData2} />

                        <button className='btn btn-info mt-5'
                            {...show ? {
                                style: {
                                    display: "block",
                                    maxWidth: "40px",
                                    height: "40px",
                                    borderRadius: "50%",
                                    position: "absolute",
                                    right: "420px",
                                    padding: "0"
                                }
                            } : { style: { display: "none" } }}>âž•</button>
                    </Row>
                </Row>
            </Container>
            <h1>{data}</h1>
            <h2>{data2}</h2>
        </section>
    )
}
