import React from 'react'
import Container from 'react-bootstrap/Container';
import { Row } from 'react-bootstrap';

export const Keep = () => {
    const [show, setShow] = React.useState(false);
    return (
        <section className="mt-5">
        <Container>
            <Row className='justify-content-center flex-column align-items-lg-center '>
                <input type="text" className='bg-white text-dark' style={{width:"450px", height:"60px"}} placeholder="Title" onFocus={setShow(true)}/>
                <textarea className='bg-white text-dark' style={{width:"450px", height:"100px", resize:"none"}} {...show ? {style:{display:"block"}} : {style:{display:"none"}}} />
            </Row>
        </Container>
        </section>
    )
}
